import React, { useState } from "react";
import "./CreatePost.css";
import { supabase } from "../client";

const CreatePost = () => {
  const [post, setPost] = useState({ title: "", author: "", description: "" });
  const [avengers, setAvengers] = useState([
    { name: "Iron Man", selected: false },
    { name: "Captain America", selected: false },
    { name: "Thor", selected: false },
    { name: "Hulk", selected: false },
    { name: "Black Widow", selected: false },
    { name: "Hawkeye", selected: false },
    { name: "Spider-Man", selected: false },
    { name: "Doctor Strange", selected: false },
    { name: "Black Panther", selected: false },
    { name: "Captain Marvel", selected: false },
  ]);

  const handleChange = (event, avenger) => {
    event.preventDefault();
    setAvengers((prevAvengers) =>
      prevAvengers.map((prevAvenger) => {
        if (prevAvenger.name === avenger) {
          return { ...prevAvenger, selected: true };
        } else {
          return { ...prevAvenger, selected: false };
        }
      })
    );
    setPost((prevPost) => {
      return {
        ...prevPost,
        description: avenger,
      };
    });
  };

  const createPost = async (event) => {
    event.preventDefault();

    const { error } = await supabase
      .from("Posts")
      .insert({
        title: post.title,
        author: post.author,
        description: post.description,
      })
      .select();

    if (error) {
      console.log(error);
    }

    window.location = "/";
  };

  return (
    <div>
      <form>
        <label>Title</label> <br />
        <input
          type="text"
          id="title"
          name="title"
          value={post.title}
          onChange={handleChange}
        />
        <br />
        <br />
        <label>Author</label>
        <br />
        <input
          type="text"
          id="author"
          name="author"
          value={post.author}
          onChange={handleChange}
        />
        <br />
        <br />
        <label>Description</label>
        <br />
        {avengers.map((avenger) => (
          <button
            key={avenger.name}
            id={avenger.name}
            className={avenger.selected ? "selected" : ""}
            onClick={(e) => handleChange(e, avenger.name)}
          >
            {avenger.name}
          </button>
        ))}
        <br />
        <input type="submit" value="Submit" onClick={createPost} />
      </form>
    </div>
  );
};

export default
 CreatePost;
